mapper.select:
   
    SELECT  ,s.`stu_name`,c.`class_name`,p.`profession_name` FROM t_holiday h
	LEFT JOIN t_student s ON h.`stu_id` = s.`id`
	LEFT JOIN t_class c ON c.`id` = s.`class_id`
	LEFT JOIN t_profession p ON p.`id` = s.`profession_id`
	
		
		  and h.stu_id = ${stuId}
		
		
		  and h.status = ${status}

mapper.insert:
insert into t_holiday (id, stu_id, tea_id, 
  begin_time, end_time, day_num, 
  content, status, aduit_name
  )
values (#{id,jdbcType=INTEGER}, #{stuId,jdbcType=INTEGER}, #{teaId,jdbcType=INTEGER}, 
  #{beginTime,jdbcType=VARCHAR}, #{endTime,jdbcType=VARCHAR}, #{dayNum,jdbcType=VARCHAR}, 
  #{content,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{aduitName,jdbcType=VARCHAR}
  )
